    return (1-t)*p1+t*p2;
}

vector gen_bezier( vector p[]; float t ){ 
    vector ps[] = p;  // Note: p is reference, so we need a new variable
    int iter = len(ps);
    for(int z=0; z<iter; z++){
        int n=len(ps);
        vector tmp[];
        for(int i=0;i<n-1;i++){
            vector cr = vector(compute(ps[i], ps[i+1], t));
            push( tmp, cr); 
        }
        ps=tmp;
        iter--; 
    }

    return compute(ps[0], ps[1], t);
}


